vec3 sampleEnvMap(samplerCube tex, vec3 R)
{
  vec3 c = vec3(1.0);
  #if USE_RGBE_CUBEMAP
    c *= unpackRGBE(fragTextureLod(tex, R, 0.0));
  #else
    c *= SRGBToLinear(fragTextureLod(tex, R, 0.0).rgb);
  #endif
  return c;
}
vec4 SurfacesFragmentModifyBaseColorAndTransparency()
{
  vec3 c = vec3(1.0);
  vec3 normal = normalize(FSInput_worldNormal);
  vec3 rotationDir = RotationVecFromAxisY(normal.xyz, CC_SURFACETRANSFORM.z, CC_SURFACETRANSFORM.w);
  c = sampleEnvMap(environmentMap, rotationDir);
  #if BLEND_ENV_MAP
    c = mix(c, sampleEnvMap(blendEnvironmentMap, rotationDir), params.x);
  #endif
  return vec4(c * CC_AMBIENTSKY.w * params.y, 1.0);
}
#ifndef CC_SURFACES_FRAGMENT_MODIFY_BASECOLOR_AND_TRANSPARENCY
vec4 SurfacesFragmentModifyBaseColorAndTransparency()
{
	return vec4(CC_AMBIENTSKY.xyz * CC_AMBIENTSKY.w, 1.0);
}
#endif
void main() {
  vec4 color = SurfacesFragmentModifyBaseColorAndTransparency();
  color.a = 1.0;
  #if CC_USE_FOG != 4
    float fogFactor = 1.0;
    CC_TRANSFER_FOG_BASE(vec4(FSInput_worldPos, 1.0), fogFactor);
    CC_APPLY_FOG_BASE(color, fogFactor);
  #endif
  #if CC_USE_RGBE_OUTPUT
    color = packRGBE(color.rgb);
  #else
    color.rgb = HDRToLDR(color.rgb);
    color.rgb = LinearToSRGB(color.rgb);
  #endif
  gl_FragColor = color;
}
